<style>
section {
        margin-top: 27px;
}
div {
        padding-top: 2px;
        padding-bottom: 2px;
}
.content-wrapper {
        max-width: 1000px;
        margin: 0 auto;
    }
.header {
        display: flex;
        flex-direction: row;
        align-items: center;
}
h1 {
        font-size: 45px;
        font-style: normal;
        font-weight: 500;
        line-height: 45px; /* 100% */
        margin-top: 25px;
        margin-bottom: 25px;
    }
h2 {
        font-size: 32px;
        font-weight: 500;
        color: var(--body);
        margin-bottom: 50px;
}
p {
        margin-top: 20px;
        margin-bottom: 24px;
        color: var(--body);
}
a {
        color: var(--victory-purple)
}
.icon {
        height: 65px;
        width: 65px;
        margin-right: 15px;
        border: 1px solid rgba(129, 83, 182, 0.505);
        border-radius: 10px;
        background: linear-gradient(180deg, rgba(59, 53, 74, 0.50) 0%, #292929 100%);
        box-shadow: 0px 0px 100px 0px rgba(181, 154, 255, 0.40);
}
.onboarding {
        background-color: var(--secondary-container);
}
.info {
        margin: 30px auto;
        padding: 0 20px;
        
    }
.phone img {
        object-fit: fill;
        width: 226px;
        border-radius: 5px;
        /* border: 1px solid rgba(214, 198, 255, 0.35); */
        background: linear-gradient(180deg, rgba(59, 53, 74, 0.50) 0%, #292929 100%);
        box-shadow: 0px 0px 100px 0px rgba(181, 154, 255, 0.40);
}
.phone-row {
        text-align: center;
        display: flex;
        justify-content: space-between;
        margin: 0 auto;
}
.phone {
        position: relative;
        height: 518px;
        width: 251px;
}
.phone.sideways > svg{
        transform: translate(-50%, -50%) rotate(90deg);
}
.phone > * {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
}
.phone img {
        transform: translate(-50%, -50%);
}
